import { ArbitraryCodec } from './arbitrary';

export const jsonCodec: ArbitraryCodec = {
  contentType: ['application/json'],
  responseType: 'json',
  encode(data: any) {
    return JSON.stringify(data);
  },
  decode(payload: Uint8Array | string) {
    return JSON.parse(payload + '');
  },
};
